// Fork of "Quick monday hack" by mrange. https://shadertoy.com/view/dld3Rs
// 2023-05-02 03:12:14

// CC0: Quick monday hack
//  Saw this tweet: https://twitter.com/junkiyoshi/status/1652996530146787329?s=20
//  Thought I could do something like.
//  It's not quite good enough but good enough to share
#define TIME        iTime*1.2
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))


vec3 hsb2rgb( in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return (c.z * mix( vec3(1.0), rgb, c.y));
}


// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

float df(vec2 p, float tm) {
  p *= ROT(-tm*0.5);
  float d = -p.y;
  return abs(d)-0.003;
}

vec3 bubbles(vec3 col, vec2 p) {

 vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;


  float aa = 4.0/RESOLUTION.y;
  for (float i = 1.0; i < 11.0; ++i) {
    float sz = 0.5/(1.0+i);
    vec2 pp = p;
    pp /= sz;
    vec2 rp = round(pp);
    vec2 cp = pp;
    cp -= rp;
    float dt = 0.01;
    float dp = df(rp*sz, TIME-dt);
    float dn = df(rp*sz, TIME);
    float dd = (dn-dp)/dt;
    float h0 = hash(rp);
    float h1 = fract(667.0*h0);
    float h2 = fract(1667.0*h0);
    float r  = mouse.x/mouse.y*0.533*sqrt(h0);
    r *= smoothstep(0.1, 0.9, length(p));
//    r *= tanh(1.0*dd);
    cp -= (0.8-r)*vec2(h1, h2);
    float fo = smoothstep(12.0*sz*r, -0.1, dn/2.);
    float d = (length(cp)-mix(r, 0., fo*fo*fo));

    float randcol = r*h0;//p.x/p.y; //hash(p) * 300.0;//round((p.x*300.)/5.0);

    d = abs(d);
    d *= sz;
    d -= 0.003;
    vec3 bcol = /*vec3(sqrt(fo)) * */ hsb2rgb(vec3(randcol*PI*2.,1.0,sqrt(fo)*0.8))*smoothstep(.0, -aa, d)*step(.0, dd)*smoothstep(-0.05, 0.1, dn);
    col += bcol;
  }
  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  float ds = df(p, TIME);
  vec3 col = vec3(0.0);
  col = bubbles(col, p);
  float aa = 4.0/RESOLUTION.y;
  //blueline//col = mix(col, vec3(0.0, 0.0, 0.725), smoothstep(0.0, -aa, ds));
  col = sqrt(col) * 2.;
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  fragColor = vec4(col, 1.0);
}
