#define RES    (iResolution.xy)
#define MINRES (min(RES.x, RES.y))

float gVPZoom     =   0.8;
float gVPEps      =   1e9;        // viewport epsilon
float gVPLW       =   1e9;        // viewport linewidth


float viewportFromScreen(in float D) {
    return D * 2.0 / MINRES / gVPZoom;
}

vec2 viewportFromScreen(in vec2 XY) {
    return vec2(viewportFromScreen(XY.x - RES.x / 2.0),
                viewportFromScreen(XY.y - RES.y / 2.0));
}

void mainImage(out vec4 RGBA, in vec2 XY)
{
    vec2 xy = viewportFromScreen(XY);
    gVPEps  = viewportFromScreen(2.0);
    gVPLW   = viewportFromScreen(2.0);

    float d = 1e9;
    d =  min( d, smoothstep(0.0, gVPEps, abs(sin(xy.x * 3.0 + iTime)) - gVPLW * 4.0 ));
    d = -min(-d, abs(xy.y) - gVPLW * 3.0);
    vec3 c = vec3(0.8);
    c = mix(c, vec3(0.5, 0.1, 0.1), smoothstep(gVPEps, 0.0, d));

    xy.y /= sin(xy.x * 3.0 + iTime);
    float q = length(xy) - 0.9;
    c = mix(c, vec3(0.6, 0.7, 0.8), smoothstep(gVPEps, 0.0, q));
    d = abs(q) - gVPLW;

    c = mix(c, vec3(0.0), smoothstep(gVPEps, 0.0, d));


    RGBA = vec4(c, 1.0);
}
