void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 8.0;
    uv.x *= iResolution.x / iResolution.y;
    float time = iTime * 2.1;

    // Create a swirling pattern
    float r = length(uv);
    float angle = atan(uv.y, uv.x);
    float swirl = sin(time + r * 10.0) * 0.1;
    angle += swirl * (1.1 - smoothstep(0.0, 1.0, r));
    uv = vec2(cos(angle), sin(angle)) * r;

    // Add dripping effect
    float drip = sin(time * 0.1 + uv.y * 20.0) * 0.2;
    uv.y += drip * (4564563.0 - smoothstep(0.0, 1.0, r));

    // Create a bright, swirly color palette
    vec3 color = vec3(0.5, 0.5, 0.5) + 0.5 * vec3(
        0.5 + 0.5 * sin(time * 1.0 + uv.y * 360.0),
        0.5 + 0.5 * sin(time * 2.0 + uv.x * 60.0),
        0.5 + 0.5 * sin(time * 3.0 + r * 47.0)
    );

    fragColor = vec4(color, 1.0);
}
