vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.y += (iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    //uv = mod(uv,1.);
    vec2 uv1 = uv;
    for(int k = 0; k < 15; k++){
        uv =
            abs(.5-uv+t2*sign(uv.y-uv.x))/1.5+1.
        ;
        b = uv.y<uv.x;
        if(b) uv = uv.yx;
        t2 =
            -triangle_wave(uv+.5*sign(uv.x-uv.y)).yx
        ;
        uv =
            t2-triangle_wave(-uv.yx).yx
        ;
        col.x =
            max(sign(uv.x),col.x)
            //abs(sign(uv.x)-col.x)/2.
        ;
        col.xyz =
            abs(col.yzx-float(b)/2.)
        ;
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}

float fmod(float a, float b){
    return mod(floor(-a),b);
}

vec2 mainSound(int samp, float time){
  float s1 =
      4.,
  s2 = 8.,
  t = time,
  m1 =
      0.
  ;
  for(int i = 0; i < 3; i++){

      t += floor(t/2.);
      m1 =
      fmod(t*2.,s2);

      t = t/(1.+fmod(m1+floor(t*2.)+floor(t/2.),2.));
  }
  float
  m4 =
      fmod(t*s1,s1)
  ,
  m3 =
      fmod((t+m4)*s1,s1)
  ;
  t *=
      s1*s1
  ;
  float m2 =
      1. + fmod(t/s1,s1/2.);
  ;
  float a=
      pow((1.-sqrt(fract(t/s1)/5.))/5.,2.)
  ;
  float nb = pow(2.,(m3+m2-m1)/5.+8.);
  return
      vec2(fract(time*nb*.998+m2/2.)*a,fract(time*nb+m2/2.)*a)
  ;
}
