// Pixel Art Shader
// Feel free to use, just give credit as always
// - TPG45

#define COLOR_STEP 6.0
#define PIXEL_SIZE 4.0

vec4 colorize(in vec4 color) {

    // Pixel art coloring
    vec3 nCol = normalize(color.rgb);
    float nLen = length(color.rgb);
    return vec4(nCol * round(nLen * COLOR_STEP) / COLOR_STEP, color.w);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Pixel Sizing
    float ratio = iResolution.y/720.0;
    vec2 pixel = round(fragCoord / (PIXEL_SIZE * ratio)) * PIXEL_SIZE * ratio;
    vec2 uv = pixel/iResolution.xy;
    fragColor = colorize(texture(iChannel0, uv));
}

uint raySphere(in Sphere sph, inout Record rec) {

    vec3 offset = rec.ray.origin - sph.center;
    float a = 2.0 * dot(offset, rec.ray.normal);
    float b = dot(offset, offset) - sph.radius * sph.radius;
    float disc = a * a - 4.0 * b;

    if (disc > 0.0) {

        float s = sqrt(disc);
        float dstNear = max(0.0,(-a-s)/2.0);
        float dstFar = (-a+s)/2.0;

        if(dstNear < rec.dist) {

            if(dstNear > 0.0) {

                rec.intersect = (rec.ray.normal * dstNear) + rec.ray.origin;
                rec.normal = normalize(rec.intersect - sph.center);
                rec.dist = dstNear;
                rec.material = sph.material;
                rec.hit = true;
                rec.offset = (rec.intersect - sph.center - sph.radius) / sph.radius - 0.5;
                rec.tangent = normalize(vec3(rec.normal.z, 0.0, -rec.normal.x));
                rec.bitangent = normalize(cross(rec.normal, rec.tangent));

                return uint(0x1);

            }

        }

        return uint(0x0);

    }

}

void distances(inout Record rec) {

    raySphere(Sphere(Material(vec3(0.0, 0.5, 1.0), true, 0, 10.0, 0.42, 0.75), vec3(200.0, 0.0, 0.0), 100.0), rec);
    raySphere(Sphere(Material(vec3(0.0, 0.5, 1.0), true, 1, 40.0, 0.42, 0.40), vec3(0.0, 0.0, 200.0), 100.0), rec);
    raySphere(Sphere(Material(vec3(0.0, 0.5, 1.0), true, 2, 10.0, 0.42, 0.80), vec3(-200.0, 0.0, 0.0), 100.0), rec);
    raySphere(Sphere(Material(vec3(0.0, 0.5, 1.0), true, 3, 10.0, 0.42, 0.60), vec3(0.0, 0.0, -200.0), 100.0), rec);

}

void initRay(inout Ray ray, in vec2 fragCoord) {

    ray.origin = vec3(fragCoord, -500.0);
    ray.normal = vec3(0.0, 0.0, 1.0);
    mat2 rot = rot2(-0.392699081699);
    ray.origin.yz *= rot;
    ray.normal.yz *= rot;

}

void initRecord(inout Record rec, in Ray ray) {

    rec.ray = ray;
    rec.hit = false;
    rec.dist = 100000.0;

}

vec4 triPlanar(sampler2D tex, vec3 offset, vec3 normal, float scale, vec2 imgOffset) {

    vec3 blend = normalize(max(abs(normal), 0.000001));
    float sum = blend.x + blend.y + blend.z;
    blend /= sum;
    vec4 x = texture(tex, offset.yz * scale + imgOffset);
    vec4 y = texture(tex, offset.xz * scale + imgOffset);
    vec4 z = texture(tex, offset.xy * scale + imgOffset);
    return x * blend.x + y * blend.y + z * blend.z;

}

vec4 texturize(int id, vec3 offset, vec3 normal, float scale, vec2 imgOffset) {

    switch(id) {
    case 0:
        return triPlanar(iChannel0, offset, normal, scale, imgOffset);
    case 1:
        return triPlanar(iChannel1, offset, normal, scale, imgOffset);
    case 2:
        return triPlanar(iChannel2, offset, normal, scale, imgOffset);
    case 3:
        return triPlanar(iChannel3, offset, normal, scale, imgOffset);
    }
    return vec4(0.0);

}

float getHeight(vec4 tex) {

    return dot(tex.rgb, vec3(.2126, .7152, .0722));

}

vec3 normal(int id, vec3 offset, vec3 normal, float scale, vec2 res, float strength) {

    float current = getHeight(texturize(id, offset, normal, scale, vec2(0.0)));
    float x = (getHeight(texturize(id, offset, normal, scale, vec2(1.0 / res.x, 0.0))) - current) * strength;
    float y = (getHeight(texturize(id, offset, normal, scale, vec2(0.0, 1.0 / res.y))) - current) * strength;
    return normalize(vec3(x, y, 1.0));

}

vec3 worldSpace(vec3 map, vec3 t, vec3 b, vec3 n) {

    mat3x3 tbn = mat3x3(t.x, b.x, n.x, t.y, b.y, n.y, t.z, b.z, n.z);
    return normalize(map * tbn);

}

vec4 Trace(in vec2 fragCoord, vec3 iChannelResolution[4], float iTime) {

    Ray ray;
    Record rec;
    initRay(ray, fragCoord);
    initRecord(rec, ray);
    distances(rec);
    if(rec.hit) {
        vec3 matColor;
        vec3 nMap;
        if(rec.material.useTexture) {
            matColor = texturize(rec.material.texID, rec.offset, rec.normal, rec.material.texScale, vec2(0.0)).xyz;
            nMap = normal(rec.material.texID, rec.offset, rec.normal, rec.material.texScale, iChannelResolution[3].xy, rec.material.bumpStrength);
            rec.normal = worldSpace(nMap, rec.tangent, rec.bitangent, rec.normal);
        } else {
            matColor = rec.material.color;
        }
        vec2 mPos = iMouse.xy - iResolution.xy/2.0;
        vec3 lightNormal = normalize((vec3(mPos.x, 400.0, mPos.y)) - rec.intersect);
        float shade = clamp(dot(lightNormal, rec.normal), 0.0, 1.0);
        float specang = acos(shade);
        float specexp = specang / (1.0-rec.material.specular);
        float spec = exp(-specexp * specexp) * rec.material.specular;
        vec3 result = matColor * mix(vec3(0.2), vec3(1.0), shade) + vec3(spec);
        return vec4(result, 1.0);
    }
    return vec4(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.y / 720.0;
    fragColor = Trace((fragCoord-(iResolution.xy)/2.0)/ratio, iChannelResolution, iTime);
}

#define BUMPSTRENGTH 10.0

struct Material {

    vec3 color;
    bool useTexture;
    int texID;
    float bumpStrength;
    float texScale;
    float specular;

};

struct Sphere {

    Material material;
    vec3 center;
    float radius;

};

struct Ray {

    vec3 origin;
    vec3 normal;

};

struct Record {

    Ray ray;
    bool hit;
    Material material;
    vec3 normal;
    vec3 intersect;
    float dist;
    vec3 offset;
    vec3 tangent;
    vec3 bitangent;

};

mat2 rot2( float a ){ vec2 v = sin(vec2(1.570796, 0) + a);	return mat2(v, -v.y, v.x); }
