float rectSDF(vec2 st, vec2 s) {
    st = st * 2.0 - 1.0;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}

float fill(float x, float size) {
    return step(x, size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 S = vec2(0.4, 0.4369);

    float hOffset = 0.2;
    float vOffset = 0.22;

    float rectALeft = rectSDF(uv - vec2(-hOffset, vOffset), S);
    float rectARight = rectSDF(uv - vec2(hOffset, vOffset), S);
    float rectBLeft = rectSDF(uv - vec2(-hOffset, -vOffset), S);
    float rectBRight = rectSDF(uv - vec2(hOffset, -vOffset), S);

    float MX = iMouse.x / iResolution.x;

    float extandableRectA = rectSDF(uv - vec2(-hOffset + MX * 0.3, vOffset), S + vec2(MX * 2.0, 0.0));
    float extandableRectB = rectSDF(uv - vec2(-hOffset + MX * 0.3, -vOffset), S + vec2(MX * 2.0, 0.0));

    vec3 leftAColor = vec3(0.973, 0.737, 0.776);
    vec3 rightAColor = vec3(0.945, 0.467, 0.525);
    vec3 leftBColor = vec3(0.647, 0.851, 0.945);
    vec3 rightBColor = vec3(0.431, 0.729, 0.871);

    vec3 middleAColor = vec3(0.969, 0.6, 0.663);
    vec3 middleBColor = vec3(0.584, 0.82, 0.929);

    vec3 color = vec3(
        fill(rectALeft, 1.0) * leftAColor
        + fill(rectARight, 1.0) * rightAColor
        + fill(rectBLeft, 1.0) * leftBColor
        + fill(rectBRight, 1.0) * rightBColor
    );

    float s = 0.3;
    float midALeft = fill(extandableRectA, s);
    float midARight = fill(rectARight, s);
    float midBLeft = fill(extandableRectB, s);
    float midBRight = fill(rectBRight, s);

    color = (midALeft == 1.0 || midARight == 1.0) ? middleAColor : color;
    color = (midBLeft == 1.0 || midBRight == 1.0) ? middleBColor : color;

    fragColor = vec4(color, 1.0);
}
