#define I iResolution
#define T iTime
#define PI 3.1415
#define TAU PI * 2.
#define rr( a ) mat2( cos(a), sin(a), -sin(a), cos(a) )
#define S smoothstep

// V IQ V

float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b, a, h) - r*h*(1.-h);
}

float smax(float a, float b, float k) {
    return -smin(-a, -b, k);
}

float rep(inout vec2 p, float rep) 
{

    float an = TAU/rep;

    float fa = (atan(p.y,p.x)+an*0.5)/an;
    float ia = floor( fa );

    float sym = an*ia;

    if( p.y < 0. ){
        sym = 0.;
    }

    p *= rr(sym);

    return ia;
}

float ligBubble( vec2 u ){
    return min(
        smin(
            length( u + vec2( 0., -.15 ) ) - .2,
            max(
                abs( u.x ) - .1, abs( u.y ) - .2
            ),
            .08
        ),
        length( u + vec2( 0., .2 ) ) - .1
    );
}

float lig( vec2 u ){

    u.y -= .15;

    u *= rr( PI * .08 );

    float i = rep( u, 11. );
    u.x -= .4;
    u.x += cos( T * ( i - 9. ) ) * .01;
    u *= rr( PI * .5 );

    return max(
        abs( u.x ) - .05 * abs( u.y - .5 ),
        abs( u.y ) - .1
    );
}

float mainVisual( vec2 u ){
    u.x += .1;
    u.y -= .1;

    u *= rr( cos( T ) * .1 );

    return abs(
        min(
            ligBubble( u ),
            lig( u )
        )
    );
}

float paperPlane1( vec3 p ){
    p.x = abs( p.x ) - .01;
    p.y -= .4;
    p.xy *= rr( PI * .03 );
    p.y += .4;

    p.yz *= rr( PI * .01 );

    return max(
        sdTriangle( p.xy, vec2( 0., .5 ), vec2( .25, 0. ), vec2( 0., 0. ) ),
        abs( p.z ) - .001
    );
}

float paperPlane2( vec3 p ){
    p.z -= .15;
    p.x = abs( p.x ) - .0;

    p.yz *= rr( PI * -.08 );

    p.y -= .4;
    p.xz *= rr( PI * -.45 );

    p.y += .4;

    return max(
        sdTriangle( p.xy, vec2( 0., .5 ), vec2( .15, 0. ), vec2( 0., 0. ) ),
        abs( p.z ) - .001
    );
}

vec3 update( vec3 p, float T ){
    p.z -= .5;
    p.y += cos( T * 1.5 ) * .2;
    p.x -= cos( T ) * 4.;
    p.z -= sin( T ) * 1.5;

    return p;
}

float paperPlane( vec3 p ){

    // trians
    p = update( p, T );
    p.xz *= rr( atan( cos(T) * .2, sin(T) ) );
    p.yz *= rr( cos( T * -3. ) * .2 + .5 );


    // pose
    p.yz *= rr( PI * .25 );
    p.xy *= rr( PI * .5 );

    return min(
        paperPlane1( p ),
        paperPlane2( p )
    );
}

float rule1( vec2 u ){
    u.x -= .5;
    u.y += .8;
    u *= rr( PI * .2 );
    return abs( sdTriangle( u, vec2( 0., .5 ), vec2( .25, 0. ), vec2( 0., 0. ) ) );
}


float lo( vec2 u ){

    u.x += .7;
    u.y += .5;

    return abs( 
        sin( atan( u.x, u.y ) - length(u) * 80. + T * .5 )
    );
}

float lo_1( vec2 u ){
    u.x += .7;
    u.y += .5;

    return length( u ) - .2;
}

float lo2( vec2 u ){
    u.x += .7;
    u.y += .5;

    u *= rr( PI * -.1 );

    return abs( 
        min(
            length( u ) - .22,
            max(
                abs( u.x ) - .015,
                abs( u.y + .3 ) - .2
            )
        )
    );
}

float rule2( vec2 u ){
    u.x -= .8;
    u.y += .5;
    u *= rr( PI * -1.3 );
    return abs( 
        max(
            max(
                length( u ) - .2,
                u.y - .035
            ),
            -(
                max(
                    length( u ) - .13,
                    u.y + .02
                )
            )
        )
    );
}

float pen( vec3 p ){

    p.x += .2;
    p.y += .6;

    p.xz *= rr( T );
    p.yz *= rr( T );

    return max(
        length( p.xz ) - .03 * ( abs( p.y - .6 ) + .2 ),
        abs( p.y ) - .2
    );
}


float pt( vec2 u ){

    u.x -= .52;
    u.y += .3;

    float py = u.y;

    py = pow( abs( py - .3 ), 2. ) + .1;

    py *= ( u.y / 2. + .5 ) / .1;

    return abs(
        smax(
            length( u.x ) - .1 * py,
            abs( u.y ) - .3,
            .1 * abs( u.y - .5 )
        )
    );
}


float pt2( vec2 u, float a ){

    u.x -= .5;
    u.y -= .04;

    u.y -= fract( a ) * .3;
    u.x += cos( a * 3. ) / 30.;

    return abs(
        length( u ) - .03 * ( 1. - fract( a ) * .3 )
    );
}


float cu( vec3 p, float T ){

    p = update( p, T );
    p.yz *= rr( cos( T ) * .2 );
    p.yz *= rr( atan( sin(T), cos(T) ) );


    return max(
        max(
            abs( p.y ) - .005,
            abs( p.z ) - .005
        ),
        abs( p.x ) - .03
    );
}

float trail( vec3 p ){
    float d = .5;

    for( float i = 50.; i > 0.; i-- ){
        d = min(
            d,
            cu( p, (T-.01) - i / ( 45. + round( cos(-T*2.) * 15. ) ) )
        );
    }

    return d;
}

float Tl;

float df( vec3 p ){
    float d = .1;

    d = min(
        d,
        paperPlane( p )
    );

    d = min(
        d,
        pen( p )
    );



    return d;
}


void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    float A = min( I.x, I.y );
    vec2 u = ( V * 2. - I.xy ) / A;

    vec3 c = vec3(.47, .51, .52), m = c, p, o = vec3( 0., 0., -3. ), r = vec3( u * .4, 1. );

    c = mix(
        c,
        m * 1.2,
        S(
            2. / A,
            0.,
            abs(
                mod( u.x + .1, .2 ) - .1
            )
        )
    );

    c = mix(
        c,
        m * 1.2,
        S(
            2. / A,
            0.,
            abs(
                mod( u.y + .1, .2 ) - .1
            )
        )
    );

    float t, d, i, e = 0., w = 5. / A, l = 9e9;

    for( ; i < 80.; i++ ){
        p = o + r * t,
        d = df( p );

        if( l < w && d > l + 1e-5 && p.z > 0.){
            e =  1.;
        }

        t += d * .8889;

        if( d < l && d != Tl ) l = d;

    }

    i = 0.,
    t = 0.;

    for( ; i < 16.; i++ ){
        p = o + r * t,
        d = trail( p );

        t += d;
    }

    t = clamp( t, 0., 1. );

    if( d < 1e-3 ){
        c = mix(
            c,
            m * 1.4,
            t
        );
    }

    c = mix(
        c,
        m * 1.8,
        e
    );

    c = mix(
        c,
        m * 1.8,
        S(
            4. / A,
            0.,
            mainVisual( u )
        )
    );

    c = mix(
        c,
        m * 1.8,
        S(
            4. / A,
            0.,
            pt( u )
        )
    );

    c = mix(
        c,
        m * 2. - ( fract( T ) * .5 ),
        S(
            5. / A,
            0.,
            pt2( u, T )
        )
    );


    c = mix(
        c,
        m * 2. - ( fract( T + .5 ) * .5 ),
        S(
            5. / A,
            0.,
            pt2( u, T + .5 )
        )
    );

    c = mix(
        c,
        m * 2. - ( fract( T + .8 ) * .5 ),
        S(
            5. / A,
            0.,
            pt2( u, T + .8 )
        )
    );


    c = mix(
        c,
        m * 2.,
        S(
            4. / A,
            0.,
            rule1( u )
        )
    );

    c = mix(
        c,
        m * 2.,
        S(
            4. / A,
            0.,
            rule2( u )
        )
    );

    c = mix(
        c,
        m * 1.8,
        S(
            300. / A,
            0.,
            lo( u )
        ) * step( lo_1( u ), 0. )
    );

    c = mix(
        c,
        m * 2.,
        S(
            5. / A,
            0.,
            lo2( u )
        )
    );



    // Output to screen
    U = vec4(c,1.0);
}
