#define PI 3.1415
#define TAU PI * 2.
#define rot( r ) mat2(cos(r), sin(r), -sin(r), cos(r) )

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec3 opRep( in vec3 p, in vec3 c, in vec3 l)
{
    return p-c*clamp(round(p/c),-l,l);
}

// IQ
float tri( in vec2 p, in float r )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

float rep(inout vec2 p, float rep) 
{

    float an = TAU/rep;

    float fa = (atan(p.y,p.x)+an*0.5)/an;
    float ia = floor( fa );

    float sym = an*ia;

    p *= rot(sym);

    return ia;
}


float tex( vec2 u ){

    u.y -= 1. / 3.;

    vec2 u2 = u;
    float p = 1.;
    // fpos
    for( float i = 0.; i < 3.; i++ ){
        u2.y += sqrt( 3. ) * .1;
        u2.x = abs( u2.x ) - .1;
        p = min(
            p,
            tri( u2, .1 )
        );
    }

    p = min(
        p,
        tri( u, .1 )
    );

    return p;
}
