    // faces

    return max(
        tri( p.xy, .21 ),
        abs( p.z ) - .001
    );
}

float map( vec3 p ){



    return TFace( p );
}


vec3 calcNormal( in vec3 p )
{
    const float h = 1e-5; // or some other value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // use different camera setting for CineShader
    #ifdef IS_CINESHADER

    // use the relative position of the camera to the center of the screen as ray origin
	vec3 rayOri = iCamPos;

    // screen size is 6m x 6m, or you can use iScreenSize.xy(CineShader only) to get the screen size
	vec3 rayDir = normalize(vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 1.0, 0.0) - iCamPos);

    // make the maxDepth further
    float maxDepth = 30.0;
    #else
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 1.0, 3.0);
	vec3 rayDir = vec3(0.0, 0.0, -1.0);
    float maxDepth = 6.0;
    #endif

	float depth = 0.0, dist;
	vec3 p;

	for(int i = 0; i < 64; i++) {
		p = rayOri + rayDir * depth;

        p.x = abs( p.x ) - .25,
        p.xz *= rot( iTime ),
        rep( p.xz , 3. ),
        p.x -= .105,
        p.y += .1,
        p.xz *= rot( PI * .5 ),
        p.yz *= rot( PI * .11 ),
        p.y -= .1;

		dist = map(p);


        depth += dist;
		if (dist < 1e-6) {
			break;
		}
	}

    depth = min(maxDepth, depth);
	vec3 n = calcNormal(p);
    float b = max(0.0, dot(n, vec3(0.577)));
    vec3 col = (0.5 + 0.5 * cos((b + iTime * 3.0) + uv.xyx * 2.0 + vec3(0,2,4))) * (0.85 + b * 0.35);


    if( dist < 1e-3 ){
        col -= vec3( step( tex( p.xy * 2. ), 0. ) * max( -calcNormal( p ).z, 0. ) ) * .2;
    }

    col *= exp( -depth / maxDepth );

    #ifdef IS_CINESHADER
    // set the screen thickness to zero in CineShader
    fragColor = vec4(col, 0.0);
    #else
    // maximum thickness is 2m in alpha channel
    fragColor = vec4(col, 1.0 - (depth - 0.5) / 2.0);
    #endif
}

