{
    o = vec4( .7, 7, 70, 700)/9.;
    float l, T = iTime*o.z;

	vec2 R = iResolution.xy,
        u = (i+i - R) / R.y * vec2(2, 4),
        r = mat2( cos( T * o.y + vec4(0,11,33,0)) ) * u,
        z = r * o.x * vec2( 1, o.y ),
        c = cos( T * o.x + sin( 1. / ( o.y + sin( T * o.x ) * (l=length( u )) ) ) ) / length( z )  * o.zz  * ( 1. - 1. /l ),
        Z = z;


	for(l=0.; l < o.w && dot( z, z ) < 7e3 / 9. ; l += o.y )
		z = (z * z * z - 3. * z * (z * z).yx -c.y * Z.yx) * vec2(1,-1)+c.x * Z;
    u = min(u+vec2(u.x,0),u+u);

	o = ( 1. - exp( -pow( (l-o) / o.w, o ).y * vec4( 1, 0, o ) * pow( max(0.,1. -  1. / length( u )) , o.y ) ) ) 
    *  pow( 1. - exp( - .25 /  dot( u *= o.x, u) ), o.z ) * o.z;
}

