float f_n_rand(float n){
    n = n+n+n*.2133;
    n = n*n*0.231;
    n = sin(n*1.2324+123.1*n)*124.123;
    return fract(n);
}
float f_n_dist(vec2 o_trn_fc_nor_offset, vec2 o_scl, vec2 o_trn){
    o_trn_fc_nor_offset*=o_scl;

    float n_index = (o_trn_fc_nor_offset.x)
        + floor(o_trn_fc_nor_offset.y) * o_scl.x;

    vec2 o = o_trn_fc_nor_offset+floor(o_trn);

    float n_index_floor = floor(o.x)
        + floor(o.y) * o_scl.x;

    float n_rand = f_n_rand(iTime*0.0001+n_index_floor/(o_scl.x*o_scl.y))-0.5;
    //n_rand = 0.;
    float n_dist = length((fract(o_trn_fc_nor_offset)-(o_trn))-0.5+n_rand*sin(iTime));
    //n_dist = pow(n_dist, 1./5.)*1.;
    //n_dist = 1.-n_dist;
    //return n_index_floor / (o_scl.x * o_scl.y);
    return n_dist;
}
vec2 f_o_index(vec2 o_trn, vec2 o_scl){
    float n_index_floor = floor(o_trn.x)
        + floor(o_trn.y) * o_scl.x;
    return vec2(
        n_index_floor, 
        n_index_floor / floor(o_scl.x*o_scl.y)
    );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_ratio_1to_iresx = 1./ iResolution.x;
    //needed for anti aliasing ?
    vec2 o_trn_fc_nor = fragCoord.xy / iResolution.xy;
    vec2 o_trn_mo_nor = iMouse.xy / iResolution.xy;

    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);
    vec2 o_trn_mo_nor_offset = (o_trn_mo_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);

    vec2 o_scl = vec2(33.,33.);

    vec2 o_scl_krn = vec2(3.,3.);
    float n_krn_xty = o_scl_krn.x*o_scl_krn.y;
    float n_dmin = 1.;
    vec2 omin = vec2(0.);
    for(float n = 0.; n < n_krn_xty; n+=1.){
        float n_x = floor(mod(n, o_scl_krn.x) - floor(o_scl_krn.x/2.));
        float n_y = floor(n / o_scl_krn.x) - floor(o_scl_krn.y/2.);
        float n_dist = f_n_dist(o_trn_fc_nor_offset, o_scl, vec2(n_x, n_y));
        //n_dmin = min(n_dist, n_dmin);
        if(n_dist < n_dmin){
            n_dmin = n_dist;
            omin = (o_trn_fc_nor_offset*o_scl) + vec2(n_x, n_y);
        }
        //n_dmin = n_dist;
    }

    fragColor = vec4(n_dmin);

    if(o_trn_fc_nor_offset.x > 0.){
        vec2 o_index = f_o_index(floor(omin), o_scl);
        float nr = f_n_rand(abs(o_index.g)*2.);
        float nr2 = f_n_rand(nr);
        fragColor = vec4(nr, nr2, 0.5, 1.);
    }
}
