vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 editHue(sampler2D tex, vec2 uv, float hueStart, float hueEnd, float brightness,float shiftHue) {
    vec4 color = texture(tex, uv);
    vec3 hsv = rgb2hsv(color.rgb);
    if (hsv.x >= hueStart && hsv.x <= hueEnd) {
        hsv.x += shiftHue;
        hsv.x = mod(hsv.x,1.0);
        hsv.y *= brightness; // TO DO: fix
        color.rgb = hsv2rgb(hsv);
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    float hueStart = 0.35;
    float hueEnd = 0.55;
    float brightness = 1.0;
    float shiftHue = sin(iTime);
    fragColor = editHue(iChannel0, uv, hueStart, hueEnd, brightness,shiftHue);
}
