void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_norperpix = vec2(1./iResolution.xy);

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trpino_nooffset = (fragCoord.xy )/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;


    vec2 o_scale = vec2(0.001);
    float n_divisions = 10.;
    float n_scale = floor(o_trpino_nooffset.x*n_divisions)/n_divisions;
    //n_scale = n_scale * 0.1;
    //o_scale = vec2(0.01+n_scale);
    vec2 o1 = vec2(sin(iTime)*0.5, 0.0);
    vec2 o2 = vec2(sin(-iTime)*0.5, 0.0);

    float n1 = length(o1-o_trpino);
    float n2 = length(o2-o_trpino);

    float n_min = min(n1, n2);
    //float n_mixed = (n1*n2);
    float ne1 = 2.;
    float n_mixed = (pow(n1, ne1)*pow(n2,ne1));
    float ne = 2.;
    n_mixed = pow(n_mixed, ne);
    n_mixed *= 1000.;

    float nstp = o_norperpix.x;
    float n_smth = smoothstep(0., nstp, n_mixed);
    fragColor = vec4(
        n_mixed, 
        1.-n_mixed, 
        n_min, 
        1.
    );
    //fragColor = sqrt(fragColor);
}
