vec4[sLen] stringBounds;

vec2 mapBounds(in vec2 uv, in vec4 drawBounds, in vec4 charBounds) {
    vec2 mappedToDrawBounds = (uv - drawBounds.xy) / (drawBounds.zw - drawBounds.xy);
    return charBounds.xy + (mappedToDrawBounds * (charBounds.zw - charBounds.xy));
}

bool inBounds(in vec2 uv, in vec4 bounds) {
    return (uv.x >= bounds.x && uv.x <= bounds.z) && (uv.y >= bounds.y && uv.y <= bounds.w);
}


vec4 calcCharBounds(in int char) {
    char += 32;
    float x = mod(float(char), 16.0);
    float y = 1.-(float(char) - x) / 16.0;
    vec2 uv = vec2(x, y) / 16.0;
    return vec4(uv, uv + 0.0625);
}



void calcStringBounds(in vec2 position, in vec2 charSize, in int stringLength) {
    vec2 topLeft = position - 0.5 * vec2(charSize.x * float(stringLength), charSize.y);
    for (int i=0; i < stringLength; i++) {
        stringBounds[i] = vec4(topLeft, topLeft + charSize);
        topLeft.x += charSize.x;
    }
}

vec3 renderText(in vec2 uv, in int stringStart, in int stringLength) {
    vec3 color = vec3(0.0, 0.0, 0.0);
    for (int i=0; i < stringLength; i++) {
        if (inBounds(uv, stringBounds[i])) {
            int charIndex = int(mod(float(stringStart + i), float(stringChars.length())));
            vec4 bounds = calcCharBounds(stringChars[charIndex]);
            vec2 mapped = mapBounds(uv, stringBounds[i], bounds);
            color += texture(iChannel0, mapped).r;
        }
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec2 pos = vec2(0.5, 0.5);//iMouse.xy/iResolution.xy;
    vec2 size = vec2(0.04, 0.08);
    calcStringBounds(pos, size, sLen);

    int nline = 0;
    while(times[nline] < mod(iTime/2., times[times.length()-1]) && nline < sLen)
        {
            nline += 1;
            nline = nline>sLen? 0: nline;
        }
    fragColor = vec4(renderText(uv, nline * sLen, sLen), 1.0);
    #ifdef debug
    fragColor = vec4(texture(iChannel0, uv).r);
    #endif
}
