#define T iTime*0.9
#define pix 1.8/iResolution.y
//vec2 dir4[4]=vec2[](vec2(1.0,0.0),vec2(0.0,1.0),vec2(0.0,-1.0),vec2(-1.0,0.0));
vec2 dir4[4]=vec2[](vec2(1.0,1.0),vec2(1.0,-1.0),vec2(-1.0,-1.0),vec2(-1.0,1.0));
float shape(vec2 uv,vec2 dir){
    vec2 l = uv-0.5;
    //vec2 dir=vec2(0);
    l+=dir;
    float thin=0.15;
    float radius=0.5;
    float a= smoothstep(radius+pix,radius-pix,length(l));
    //float a= smoothstep(pix,-pix,abs(length(l)-radius)-thin);
    return a;
}
float shape4(vec2 uv){
    float o=0.0;
    for(int i=0;i<4;i++){
        //o=max(o,shape(uv,dir4[i]*0.5));
        if(i%2==0)continue;
        o+=shape(uv,dir4[i]*0.5);
    }
    o=shape(uv,vec2(0.0));
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n=500.0;
    vec2 st = (fragCoord/iResolution.y);
    vec2 uv=fract(st);
    float r=0.1;
    vec2 p=vec2(cos(T),sin(T*0.333))*0.5+vec2(iResolution.x/iResolution.y*0.5,0.5);
    vec2 origin=0.5+floor(st);
    float l;
    vec3 col ;

    for(int i=0;i<5;i++){
        float size=pow(0.5,float(i+1));
        l=distance(p,origin)-size-r;
        if(l>0.01)break;
        origin+=(step(vec2(0.5),uv)-0.5)*size;
        uv=fract((uv-0.5)*2.0);   
    }
    col.rg=vec2(shape4(uv));
    col.b=smoothstep(pix,-pix,length(st-p)-r);;
    fragColor = vec4(col,1.0);
}
