// Fork of "plasma 2" by 104. https://shadertoy.com/view/ml33W8
// 2023-04-24 17:32:50

const float gStep = .15;
vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}



#define pi 3.14159265359 

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    vec2 uvo = uv;
    uv.x *= iResolution.x / iResolution.y;


    // allow mouse zoom and rotate    
    vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;
    uv.xy *= r2d(-(mouse.x)*pi*2.);
    uv *= (1./(10.0*mouse.y));


    uv *= 8.;
    uv += sin((vec2(uv.y, -uv.x)+iTime*.5)*4.)*.2;
    uv += 14.+iTime;

    vec2 id = floor(uv);
    o = vec4(0);

    for (float i = 0.;i<1.;i+=gStep)
    {
        uv += sin((vec2(uv.y,uv.x) + iTime*.4));

        id = floor(uv);
        vec2 p =  uv-id;
        vec4 h = hash42(id);

        float d = .707-length((p-.5));
        float d2 = step(mix(.15,.45,h.x), d);

        o = vec4(o.r + d2);
        o = abs(o-.4) * h;
    }
    o *= 1.-dot(uvo,2.*uvo);
}
