#define PI 3.14159265

//https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float sdLine(vec2 a, vec2 b, vec2 p){
    vec2 ab = b - a;
    float t = dot(p - a, ab) / dot(ab, ab);
    vec2 p2 = a + t*ab;
    return length(p - p2);
}

//https://www.shadertoy.com/view/3sSGWt
vec2 hexCoords(vec2 uv) {
	vec2 r = vec2(1, sqrt(3.0));
    vec2 h = r*0.5;

    vec2 a = mod(uv, r)-h;
    vec2 b = mod(uv-h, r)-h;

    vec2 gv = uv - (dot(a,a) < dot(b,b) ? a : b);

    return gv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.y*3.0 + 1.0;
    vec2 hexcoord = hexCoords(uv);
    uv -= hexcoord;

    float a = floor(atan(uv.y,uv.x)*3.0/PI)*PI/3.0 + PI/6.0;
    uvec2 uuv = uvec2(length(uv)*50.0,sdLine(vec2(0,0),vec2(cos(a),sin(a)),uv)*50.0 + floor(hash12(hexcoord)*200.0));
    float i = fract(float(uuv.x & uuv.y)/9.0);
    vec3 col = mix(vec3(0.784,0.933,0.996),vec3(0.357,0.557,0.745),i);

    fragColor = vec4(col,1.0);
}
