#define R iResolution.xy
#define T (iTime)
#define M_PI 3.14159265358979323846264338327950288
#define TAU (M_PI*2.0)
#define FUI floatBitsToUint
#define ZERO min(0, iFrame)

mat2 rot(float a) { float s = sin(a); float c = cos(a); return mat2(c, s, -s, c); }
vec3 aces(vec3 x) { return clamp((x*(2.51*x+0.03))/(x*(2.43*x+0.59)+0.14),0.0,1.0); }


float xgt(float a, float b, float k, float w) {
    return (1.0/(w*sqrt(((a + k) - (b - k)))));
}

float xlt(float a, float b, float k, float w) {
    return xgt(b, a, k, w);
}

float F(vec2 p) {
    p.xy *= rot(T);
    float k = sqrt(fwidth(length(p))*4.);
    float t = 0.0015;
    float w = 15.;

    return clamp(pow((xlt(p.y-t, t, k, w) * xgt(p.y+t, -t, k, w) +
           xlt(p.x-t, t, k, w) * xgt(p.x+t, -t, k, w))*8., 3.0), 0.0, 1.0);
}

void mainImage(out vec4 o, in vec2 fc) {
  vec3 col = vec3(0.0);
  vec2 uv = (fc-0.5*R.xy)/R.y;
  vec4 m = vec4((iMouse.xy-0.5*R.xy)/R.y,iMouse.zw);
  col += F(uv);
  col = aces(col);
  col = exp(col*7.-5.);
  col = pow(col, vec3(1.0 / 2.2));
  o = vec4(col, 1.0);
}
