void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord-iResolution.xy*.5)/n_scl_min;

    float n_its = 33.;
    float n_tau = radians(360.);
    float n_min = 1.;
    vec3 o_col = vec3(0.);
    uv*= 2.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.){
        float n_it_nor = n_it / n_its;
        float n_amp = sin(iTime+n_tau*n_it_nor*3.)*0.2+0.5;
        vec2 o = vec2(
            sin(n_tau*n_it_nor), 
            cos(n_tau*n_it_nor)
        )*n_amp;


        float n = length(uv-o);
        float n_radius = (sin(iTime+n_it_nor*n_tau)*.5+.5)*0.2;
        n_radius = 0.12;
        n = abs(n-n_radius);
        if(n < n_min){
            n_min = n;
        }
        float nt = iTime*0.5;
        vec3 oc = vec3(
            sin((n_it_nor+1./3.+nt)*n_tau)*2.,
            sin((n_it_nor+2./3.+nt)*n_tau)*2.,
            sin((n_it_nor+3./3.+nt)*n_tau)*2.
        );
        oc = clamp(oc, 0., 1.);
        float b = (sin(n_it_nor*n_tau*3.+iTime*5.)*.5+.5)*0.02;//0.006; 
        float n2 = b*(1./n-b);
        o_col += vec3(
            oc*n2
        );

    }
    //o_col.x = 1.-pow(n_min, 1./5.);
    // Output to screen
    fragColor = vec4(o_col,1.0);
}
