// flips in the horizontal direction
vec2 flipX(vec2 uv) {
    return vec2(1.0-uv.x, uv.y);
}

// flips in the vertical direction
vec2 flipY(vec2 uv) {
    return vec2(uv.x, 1.0-uv.y);
}

// flips in both horizontal and vertical directions
vec2 flip(vec2 uv) {
    return 1.0-uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec2 grid = vec2(2.);
    vec2 fuv = fract(uv*grid);
    ivec2 g = ivec2(uv*grid);
    int i = g.x+(g.y<<1);

    if (i == 0) fragColor = texture(iChannel0, fuv);
    if (i == 1) fragColor = texture(iChannel0, flipX(fuv));
    if (i == 2) fragColor = texture(iChannel0, flipY(fuv));
    if (i == 3) fragColor = texture(iChannel0, flip(fuv));
}
