void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/iResolution.yy;

    float n_its = 12.;

    float n_tau = 6.2831;
    vec4 o_col_min = vec4(0.);
    vec4 o_col = vec4(0.);

    for(float n_it_nor = .0; n_it_nor<1.;n_it_nor+=(1./n_its)){

        float n_radius = (sin(n_it_nor*n_tau+iTime)*.5+.5)*.8;
        float n = length(o_trn*(1./n_radius));
        n = clamp(abs(n-.5)*2., 0., 1.);
        float n_exponent = n_its;
        n = pow(1.-n, n_exponent);

        o_col += vec4(n)*vec4(n_it_nor, 1.-n_it_nor, fract(.5+n_it_nor), 1.);

    }
    fragColor = o_col;
}
