mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float hash(vec2 x)
{
   return fract(sin(dot(x.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// Color palette modified from: https://iquilezles.org/articles/palettes/
vec3 palette( in float t )
{
    vec3 a = vec3(0.8588, 0.4235, 0.5176);
    vec3 b = vec3(0.6863, 0.5294, 0.451);
    vec3 c = vec3(0.3882, 0.3882, 0.3059);
    vec3 d = vec3(2.538, 2.478, 0.168);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;

    vec2 p = (fragCoord -.5* iResolution.xy) / iResolution.y;
    p *= rot(sin(iTime/2.2));

    for (float i=0.; i<19.; i++)
    {
        float c = length(p) -.3;

        float st = .006*cos(iTime)*sin(iTime*2.);
        float a = .001*sin(iTime) + i*st;
        float hf = hash(vec2(int(iTime/2.)));
        float f = floor(hf*40.) + 15.;
        float w = abs(c - a*sin(atan(p.x, p.y)*f));

        float cwt = 13.;
        float cw = abs(sin((w+iTime*.08)*cwt)/cwt-.08);
        cw = 0.0003/cw;

        w = 0.0003/w;

        col += w + cw*palette(length(p-cos(iTime)))*.5;
    }

    fragColor = vec4(col, 1.);
}
