float C(vec2 U, float x, float y, float r,float a, float da) {         // draw arc of circle
    U -= vec2(x,y);
    return max( abs( length(U) - r ) , abs( atan(U.y,-U.x) - a ) - da );
}
float L(vec2 p, vec2 a,vec2 b) {                                       // draw line
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) );
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;                                       // normalize coordinates
    U.x = abs(U.x);
    float d = min( min(min( C(U, 0.,-1.6,  2.1,  1.57, .47 ),          // eye top curve
                            C(U, 0., .5 ,  1. , -1.57, 1.32)    ),     // eye bottom curve
                       min( C(U, 0., 2.52, 2. , -1.57, .39 ),          // eyebrow
                            C(U, 2.6, 0. , 1.4,  0.  , .45 )    )),    // sides
                       min( L(U, vec2(.65,-.27), vec2(1  ,-.65) ),
                            L(U, vec2(.28,-.47), vec2(.45,-.85) ) )
                 ) - .02;
    d = min(d, max( length( U -vec2(0,  .4) ) -  .6,                   // pupil
                    length( U -vec2(0,-1.6) ) - 2.1                    // clamp          
           )      );

    O = mix(  vec4(.9,.1,.2,1), vec4(.4,0,.4,1), clamp( R.y/6. * d, 0.,1.) ); // color scheme

}
