vec3 rot3(vec3 uv,float a){
	return vec3(uv.x*cos(a)-uv.y*sin(a) * tan(a),uv.y*cos(a)+uv.x*sin(a),uv.y*cos(a)+uv.x*sin(a));
}
float smin(float a, float b, float k){
  float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
  return mix(b, a, h) - k * h * (1.0 - h); 
}

float random (in vec2 st) {
    return fract((cos(st.x) + 3.0) * (sin(st.y) + 3.0) + fract(iTime * 0.03) * 3.0);
}

float noise (in vec2 p) {
  vec2 i = floor(p);
  vec2 f = fract(p);
  float a = random(i);
  float b = random(i + vec2(1.0, 0.0) * 3.0);
  float c = random(i + vec2(0.0, 1.0) * 3.0);
  float d = random(i + vec2(1.0, 1.0) * 3.0);
  vec2 u = smoothstep(0.,1.,f);
  u = vec2(pow(u.x, 1.0), pow(u.y, 1.0));
  return mix(mix(a, b, u[0]), mix(c, d, u[0]), u[1]);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float aspect = iResolution.y/iResolution.x;
  vec2 st = (8.0 * gl_FragCoord.xy -iResolution.xy)/ min(iResolution.x, iResolution.y);
  vec3 co = vec3(
    fract(noise(st) * 3.20),
    smin(step(.12,fract(noise(st) * 4.0)), smoothstep(0., 12.90,fract(noise(st * 1.2) * 1.0)) , .53 + abs(floor(sin(iTime * .4) * 2.0))),
    smoothstep(1.0, .0,fract(noise(st * 2.0) * 1.0)) 
  );
  co = vec3(pow(mix(co.x, co.y, co.z * 1.40), 1.0)) * rot3(co, .1);
  co = fract(rot3(co, -30.2));
  fragColor = vec4(co,  1.0);
}
