vec3 cartesian_to_barycentric(
    vec2 p,
    vec2 v0,
    vec2 v1,
    vec2 v2,
    bool clamp_,
    out bool p_is_outside
)
{
    vec3 b = vec3(
        cross2d(v1 - p, v2 - p),
        cross2d(v2 - p, v0 - p),
        cross2d(v0 - p, v1 - p)
    ) / cross2d(v1 - v0, v2 - v0);
    p_is_outside = min(min(b.x, b.y), b.z) < 0.;
    if (clamp_)
    {
        b = max(b, 0.);
        b /= (b.x + b.y + b.z);
    }
    return b;
}

vec3 cartesian_to_barycentric(
    vec3 p,
    vec3 v0,
    vec3 v1,
    vec3 v2,
    bool clamp_,
    out bool p_is_outside
)
{
    vec3 b = vec3(
        length(cross(v1 - p, v2 - p)),
        length(cross(v2 - p, v0 - p)),
        length(cross(v0 - p, v1 - p))
    ) / length(cross(v1 - v0, v2 - v0));
    p_is_outside = min(min(b.x, b.y), b.z) < 0.;
    if (clamp_)
    {
        b = max(b, 0.);
        b /= (b.x + b.y + b.z);
    }
    return b;
}

