vec3 flim_blender_rgb_to_hsv(vec3 rgb)
{
    float cmax, cmin, h, s, v, cdelta;
    vec3 c;

    cmax = max(rgb[0], max(rgb[1], rgb[2]));
    cmin = min(rgb[0], min(rgb[1], rgb[2]));
    cdelta = cmax - cmin;

    v = cmax;
    if (cmax != 0.)
    {
        s = cdelta / cmax;
    }
    else
    {
        s = 0.;
        h = 0.;
    }

    if (s == 0.)
    {
        h = 0.;
    }
    else
    {
        c = (vec3(cmax) - rgb.xyz) / cdelta;

        if (rgb.x == cmax)
        {
            h = c[2] - c[1];
        }
        else if (rgb.y == cmax)
        {
            h = 2. + c[0] - c[2];
        }
        else
        {
            h = 4. + c[1] - c[0];
        }

        h /= 6.;

        if (h < 0.)
        {
            h += 1.;
        }
    }

    return vec3(h, s, v);
}

vec3 flim_blender_hsv_to_rgb(vec3 hsv)
{
    float f, p, q, t, h, s, v;
    vec3 rgb;

    h = hsv[0];
    s = hsv[1];
    v = hsv[2];

    if (s == 0.)
    {
        rgb = vec3(v, v, v);
    }
    else
    {
        if (h == 1.)
        {
            h = 0.;
        }

        h *= 6.;
        int i = int(floor(h));
        f = h - float(i);
        rgb = vec3(f, f, f);
        p = v * (1. - s);
        q = v * (1. - (s * f));
        t = v * (1. - (s * (1. - f)));

        if (i == 0)
        {
            rgb = vec3(v, t, p);
        }
        else if (i == 1)
        {
            rgb = vec3(q, v, p);
        }
        else if (i == 2)
        {
            rgb = vec3(p, v, t);
        }
        else if (i == 3)
        {
            rgb = vec3(p, q, v);
        }
        else if (i == 4)
        {
            rgb = vec3(t, p, v);
        }
        else
        {
            rgb = vec3(v, p, q);
        }
    }

    return rgb;
}

vec3 flim_blender_hue_sat(vec3 col, float hue, float sat, float value)
{
    vec3 hsv = flim_blender_rgb_to_hsv(col);

    hsv[0] = fract(hsv[0] + hue + .5);
    hsv[1] = clamp(hsv[1] * sat, 0., 1.);
    hsv[2] = hsv[2] * value;

    return flim_blender_hsv_to_rgb(hsv);
}

