// source: https://www.shadertoy.com/view/DljyDG
// (heavily modified)

vec3 colormap(float x)
{
    vec3 c = vec3(1.0);
    c = mix(c, 1.2 * vec3(0.3, 0.5, 0.8), remap_clamp(x, -1.0, -0.6, 0.0, 1.0));
    c = mix(c, 1.2 * vec3(0.1, 0.02, 0.4), remap_clamp(x, -0.6, -0.25, 0.0, 1.0));
    c = mix(c, vec3(0.0), remap_clamp(x, -0.25, 0.0, 0.0, 1.0));
    c = mix(c, 1.2 * vec3(0.4, 0.1, 0.02), remap_clamp(x, 0.0, 0.25, 0.0, 1.0));
    c = mix(c, 1.2 * vec3(0.8, 0.5, 0.3), remap_clamp(x, 0.25, 0.6, 0.0, 1.0));
    c = mix(c, vec3(1.0), remap_clamp(x, 0.6, 1.0, 0.0, 1.0));

    c = pow(c, vec3(1.8));
    c += vec3(0.03, 0.0, 0.02);

    return c;
}

vec2 fractal_screen_to_uv(vec2 coord, vec2 render_res)
{
    return 2. * (coord - .5 * render_res) / min(render_res.x, render_res.y);
}

// Returns a value in [0, 1] which will be fed to the color map
float fractal(vec2 coord, vec2 render_res)
{
    vec2 uv = fractal_screen_to_uv(coord, render_res);

    // Number of iterations
    const int max_iters = 120;
    int iters;

    // Starting complex number
    vec2 a = uv;

    // Constant to add in each iteration
    vec2 c;
    if (iMouse.z > .5)
    {
        vec2 m = .5 * fractal_screen_to_uv(iMouse.xy, render_res);
        m /= sqrt(length(m));
        c = m;
    }
    else
    {
        c = vec2(-0.756924804733, 0.210445461405);
    }

    while (iters++ < max_iters)
    {
        // a = a^2
        a = vec2(a.x * a.x - a.y * a.y, 2. * a.x * a.y);

        // Add constant
        a += c;

        // Break if a is too large
        if (dot(a, a) > 2.5)
            break;
    }

    // Return a value based on the number of iterations 
    // done before becoming too large.
    return remap(
        exp(-float(iters) / float(max_iters)),
        1., exp(-1.),
        0., 1.15
    );
}

vec3 render(vec2 coord, vec2 render_res)
{
    vec3 col = colormap(fractal(coord, render_res));
    col = mix(vec3(dot(col, vec3(1. / 3.))), col, 1.1);
    col = max(col, 0.);
    return col;
}

