void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    ivec2 icoord = ivec2(frag_coord);
    vec2 render_res = ceil(.5 * iResolution.xy);

    if (!icoord_in_bounds(
        icoord,
        ivec2(render_res)
    ))
    {
        frag_col = vec4(0);
        return;
    }

    vec2 offs = jitter_offset(iFrame) + subsubjitter_offset(iFrame);

    vec3 col = render(
        frag_coord + offs,
        render_res
    );

    frag_col = vec4(col, 1);
}

void mainImage(out vec4 frag_col, in vec2 frag_coord)
{
    ivec2 icoord = ivec2(frag_coord);
    vec2 render_res = ceil(.5 * iResolution.xy);

    vec2 buf_coord = clamp(
        (frag_coord / iResolution.xy) * render_res,
        vec2(.501),
        render_res - .501
    );
    ivec2 buf_icoord = ivec2(buf_coord);

    vec2 sample_coord = vec2(buf_icoord) + .5 + jitter_offset(iFrame);
    vec2 sample_coord_upscaled = (sample_coord / render_res) * iResolution.xy;

    // whether the jittered sample point falls inside the boundaries
    // of the current pixel when upscaled
    bool sample_inside_this_pixel =
        sample_coord_upscaled.x >= float(icoord.x)
        && sample_coord_upscaled.y >= float(icoord.y)
        && sample_coord_upscaled.x < float(icoord.x + 1)
        && sample_coord_upscaled.y < float(icoord.y + 1);

    // in real TAA, this would also make sure that the previous
    // history (reprojected and accumulated color from the
    // previous frame) is valid, for example, by checking the
    // depth of the neighbors and whether the pixel has just
    // entered the frame, and other factors. here, we always use
    // the previous frame's value unless we're on the first frame.
    // we also don't do any reprojection in this demo.
    bool valid_history = iFrame > 0;

    // find the previous best candidate (the starting color)
    vec3 col = vec3(0);
    if (valid_history)
    {
        // we can use the (potentially reprojected)
        // values from the previous frame
        col = texelFetch(iChannel1, icoord, 0).rgb;
    }
    else
    {
        // we have nothing but the low res render buffer, so we'll
        // just use bilinear interpolation over it
        col = texture(iChannel0, buf_coord / iResolution.xy).rgb;
    }

    // if the new offset falls in this pixel, blend toward the new
    // sample
    if (sample_inside_this_pixel)
    {
        // we have an exact sample to use
        vec3 sample_col = texelFetch(iChannel0, buf_icoord, 0).rgb;

        // blend for accumulation (you could use more complex algorithms
        // for the blend weight)
        // NOTE: this blending would be useless if we didn't have the
        // sub-sub-jitter.
        col = mix(col, sample_col, .2);
    }

    frag_col = vec4(col, 1.);
}
