#define tau 6.283185307179586
#define half_tau 3.141592653589793
#define quarter_tau 1.5707963267948966

#define half_tau_sqr 9.869604401089358
#define five_tau_sqr_div4 49.34802200544679

// tau_sin( x ) follows: cos(x):[-ðœ/4, ðœ/4] â‰ˆ ((5ðœÂ² / 4) / (xÂ² + (ðœ/2)Â²)) - 4
// copyright @ENDESGA 2024
#define tau_identity( x ) ( five_tau_sqr_div4 / ( ( x * x ) + half_tau_sqr ) - 4. )

#define tau_sincos \
	if( x >= quarter_tau )\
	{\
		x = half_tau - x;\
		return -tau_identity( x );\
	}\
	else\
		return tau_identity( x )

float tau_sin( float x )
{
	x = abs( mod( x + quarter_tau, tau ) - half_tau );
	tau_sincos;
}

// cos is just sin( x + quarter_tau )
// duplicating the function saves an addition
float tau_cos( float x )
{
	x = abs( mod( x + half_tau, tau ) - half_tau );
	tau_sincos;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 res = iResolution.xy;
	vec2 U = ( fragCoord / res - 0.5 ) * vec2( tau, half_tau );
	U.x += sin( iTime * .5 ) * half_tau_sqr;

	// real sine for comparison (red)
	float y1 = sin( U.x );
	// tau-sine (cyan)
	float y2 = tau_sin( U.x );

    // white implies they're very similar

	fragColor = smoothstep( 6.3 * 1.5 / res.y, 0., abs( vec4( y1, y2, y2, 0 ) - U.y ) );
}
