#define PI 3.1415926
mat2 Rot2(float a )
{
    float c = cos( a );
    float s = sin( a );
    return mat2( c, -s, s, c );
}

// signed distance to an equilateral triangle
float sdEquilateralTriangle(  in vec2 p, in float r )
{
    p.y += 0.01;
    const float k = sqrt(3.0);
    p.x = abs(p.x) - r;
    p.y = p.y + r/k;
    if( p.x+k*p.y>0.0 ) p=vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0*r, 0.0 );
    return -length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float t = fract(iTime * 0.25) * 30.-2.5; //Time loop

    uv *= Rot2(PI*0.25); //Rotate 45 degrees 

    vec2 id = floor(uv*10.);

    float d = ((floor(id.y)-floor(id.y*0.5)*2.)-0.5)*2.;
    uv.x += max(d,0.)*0.15;

    vec2 gv = fract(uv*10.)-0.5;

    vec2 ic = floor((uv-0.5)*10.);
    ic *= Rot2(-PI*0.25);
    ic.x += t;

    float r = min(ic.x*(10.-ic.x)*0.04, .45);

    float sdTriangle = sdEquilateralTriangle(d * gv, r);

    float Tri = smoothstep(0.025, -0.025,sdTriangle);

    fragColor = vec4(1.,0.,1.,1.) * Tri;
}
