// USA Flag by Rik Riesmeijer (CC0)
float star(vec2 v) {
    v = v * 2.5;
    mat2 z = mat2(v.y, -v.x, v.x, v.y);
    z = z * z * z * z * z - mat2(1);
    return smoothstep(0.9, 9.0, determinant(z));
}

void flagUSA(out vec4 c, vec2 v) {
    v = (v + v - iResolution.xy) / min(iResolution.x, iResolution.y);
    c = (cos(20.4203522 * v.y) < 0.0)?
         vec4(1.00) : vec4(0.73, 0.08, 0.03, 1.00);
    c = (max(v.x + 2.0 / 13.0, - 1.0 / 13.0 - v.y) > 0.0) ?
         c : vec4(0.00, 0.16, 0.41, 1.00);
    v = vec2(0.95, 0.9) * v + vec2(-0.05, 0.05);
    c = (v.x * 13.0 > -21.5 && v.x * -13.0 >  3.0 &&
         v.y * -13.0 <  0.8 && v.y *  13.0 < 12.0) ?
        max(c, smoothstep(0.13, 0.1,
                          star(mod(vec2(8, 10) * v - vec2(0.25, 0.125),
                                   vec2(2)) - vec2(1, 0.6)))) : c;
    v = vec2(1.6, -1.2) / 13.0 + v;
    c = (v.x *  13.0 > -19.5 && v.x * -13.0 >  3.5 &&
         v.y * -13.0 <   0.8 && v.y *  13.0 < 10.0) ?
        max(c, smoothstep(0.13, 0.1,
                          star(mod(vec2(8, 10) * v - vec2(0.25, 0.125),
                                   vec2(2)) - vec2(1, 0.6)))) : c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    flagUSA(fragColor, fragCoord);
}
