const vec3 background = vec3(255.0, 253.0, 221.0) / 255.0;
const vec3 circle = vec3(255.0, 49.0, 67.0) / 255.0;
const float radius = 0.2;
const float PI = 3.14159265358979;
float polarAngle(in vec2 uv, in vec2 center)
{
    vec2 diff = normalize(uv - center);
    return atan(diff.y, diff.x);
}

float bias(float t, float b) {
    return (t / ((((1.0/b) - 2.0)*(1.0 - t))+1.0));
}

vec2 rotatePoint2d(vec2 uv, vec2 center, float angle)
{
    vec2 rotatedPoint = vec2(uv.x - center.x, uv.y - center.y);
    float newX = cos(angle) * rotatedPoint.x - sin(angle) * rotatedPoint.y;
    rotatedPoint.y = sin(angle) * rotatedPoint.x + cos(angle) * rotatedPoint.y;
    rotatedPoint.x = newX;
    return rotatedPoint;
}

float parabola(float x, float k)
{
    return pow(4.0 * x * (1.0 - x), k);
}

float triangle_wave(float x, float freq, float amplitude)
{
    return (abs(fract(x * freq) - 0.5) * 2.0 - 1.0) * amplitude;
}

float impulse(float x)
{
    return 8.0*x*exp(1.0-8.0*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Rotation matrix
    float rotateAngle = iTime * 25.0 * PI / 180.0;
    mat2 rotate = mat2(cos(rotateAngle), -sin(rotateAngle), sin(rotateAngle), cos(rotateAngle));
    vec2 center = vec2(iResolution.x / (iResolution.y * 2.0), 0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.y) - center;
    uv = uv * rotate + center;


    // Draw a circle
    float dist = length(uv - center);

    vec4 circleCol = vec4(0);
    float rad = polarAngle(uv, center);
    float bump = bias(triangle_wave(rad / (PI * 2.0), 5.0, 1.0), 0.2) * sin(iTime);
    if (dist < radius + bump)
        circleCol = vec4(circle, 1.0);

    vec4 backgroundCol = vec4(background, 1.0);
    // Output to screen
    fragColor = mix(backgroundCol, circleCol, circleCol.a);
}
