// Sunny Days, Sunny Nights - By Rik Riesmeijer (CC0)
float dot2(vec2 uv) { return dot(uv, uv); }

mat2 complex(vec2 uv) { return mat2(uv.x, -uv.y,  uv.y, uv.x); }
mat2 invcomp(vec2 uv) { return mat2(uv.x,  uv.y, -uv.y, uv.x) / dot2(uv); }
mat2 etaylor(mat2 z) {
    mat2 result = mat2(1.0, 0.0, 0.0, 1.0);
    float factorial = 1.0;
    mat2 zn = z;
    for(float i = 1.0; i < 10.0; i += 1.0)
        result += zn * z / i;
    return result;
}
mat2 rot(float a) { return mat2(cos(a), -sin(a), sin(a), cos(a)); }
vec4 display(mat2 z) { return smoothstep(1.6, 1.2, vec4(determinant(z))); }

void mainImage(out vec4 c, in vec2 v) {
    v = 11.0 * (v - 0.5 * iResolution.xy) / iResolution.y;

    v = (mod(v, 2.5) - 1.25) *
        rot(floor(0.4 * v.x + 33.0) + 0.5 * floor(0.4 * v.y + 41.0));

    mat2 z  = complex(v);
    mat2 zc = invcomp(v);

    c  = display(z * z * z * z * z - mat2(1, 0, 0, 1));
    c *= display(rot(1.3 * iTime) * z * z * z * z * z - mat2(1, 0, 0, 1));
    c  = mix(vec4(vec2(0.761,0.835) * (0.6 - 0.4 * cos(1.3 * iTime)),
                       1.0          * (0.7 - 0.3 * cos(1.3 * iTime)), 1),
             vec4(0.984, 1.000, 0.000, 1),
             c);

}
