// Butterfly fractal
void mainImage( out vec4 o, vec2 u )
{
    o.xyz = iResolution;
    u = (o.xy-u-u)/o.y;

    o -= .1 + o;

    for (int i; i++ < 9; o -= exp(-3.*sqrt(length(u))))
    u = vec2( sin( 3.*atan(u.x, u.y+.5*sin(5.*u.y+cos(3.*u.x))) ),
              dot(u, u) );

    o = 1.- exp(30.*o*o*o);
}

