// CC0: Simple super spherical^4 shading
//  I wanted super spherical with no ray trace step

#define TIME        iTime
#define RESOLUTION  iResolution

vec3 sphere4(vec3 col, vec2 p, float aa, float r, vec3 lightDir) {
  vec2 p2 = p*p;
  float r4 = r*r;
  r4 *= r4;
  float z4 = r4-dot(p2, p2);
  if (z4 > 0.) {
    float z = pow(z4, 1./4.);
    vec3 cp = vec3(p, z);
    vec3 cp3 = cp*cp*cp;
    vec3 cn = normalize(cp3);
    float cd= max(dot(lightDir, cn), 0.0);

    vec3 ccol = col;
    ccol = cd*cd*vec3(1.0);
    float d = pow(dot(p2, p2), 1./4.)-r;
    col = mix(col, ccol, smoothstep(0., -2.*aa, d));
  }
  return col;
}

vec3 effect(vec2 p, vec2 pp) {
  const vec3 lightDir = normalize(vec3(1.0, 1.5, 2.0));
  float aa = sqrt(2.)/RESOLUTION.y;
  vec3 col = vec3(0.025);

  col = sphere4(col, p, aa, 0.75, lightDir);
  col *= smoothstep(1.5, 0.5, length(pp));
  col = sqrt(col);
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp =p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = vec3(0.05);
  col = effect(p, pp);
  fragColor = vec4(col, 1.0);
}
