// Shader made by Maximilien "Cewein", Feel free to use it as long as you reference it
// Actual code is in Buffer A
//
///////////////////
//  ____   _____   _____   ______  __      __ _                            
// |  _ \ |  __ \ |  __ \ |  ____| \ \    / /(_)                           
// | |_) || |__) || |  | || |__     \ \  / /  _   ___ __      __ ___  _ __ 
// |  _ < |  _  / | |  | ||  __|     \ \/ /  | | / _ \\ \ /\ / // _ \| '__|
// | |_) || | \ \ | |__| || |         \  /   | ||  __/ \ V  V /|  __/| |   
// |____/ |_|  \_\|_____/ |_|          \/    |_| \___|  \_/\_/  \___||_|                                                                           
// 
///////////////////
//
// This shader is the base for any BRDF you want to display
// It provide code (in buffer A) that should be easy enough to understand
// Be aware, the scene is render with Raymarching, it migth change in the futur.
//
// Version 1.0 - 21/04/2024
// Version 1.1 - 25/04/2024 : move brdf to its own function
//
///////////////////
//
// Possible improvement :
// - better controle over the BRDF
// - implement spatio-temporal denoising
//


///// TONEMAPPING OPERATION /////

// linear white point
const float W = 1.2;

float reinhard_curve (float x) {
	return x / (1.0 + x);
}

vec3 reinhard(vec3 x) {
    float w = reinhard_curve(W);
    return vec3(
        reinhard_curve(x.r),
        reinhard_curve(x.g),
        reinhard_curve(x.b)) / w;
}

///// MAIN IMAGE /////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = texture( iChannel0, uv ).xyz;
    col /= texture( iChannel0, uv ).w;

    //tone mapping
    col = reinhard(col);


    fragColor = vec4(col, 1.0 );
}

#define PI atan(1.0)*4.0

///// COMMUN /////

float saturate (float x)
{
    return min(1.0, max(0.0,x));
}
vec3 saturate (vec3 x)
{
    return min(vec3(1.,1.,1.), max(vec3(0.,0.,0.),x));
}

mat2 rot(in float a)
{
    a *= 0.0174533;
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

///// STRUCT /////

struct hitPayload
{
    vec3 orig;
    vec3 hitPos;
    vec3 dir;
    vec3 normal;
    int matIndex;
    int nbStep;
    float dist;
};

struct material
{
    vec3 albedo;
    float metalness;
    float roughness;
    float IOR;
};

///// SPECTRAL TO RGB /////

// --- Spectral Zucconi --------------------------------------------
// By Alan Zucconi
// Based on GPU Gems: https://developer.nvidia.com/sites/all/modules/custom/gpugems/books/GPUGems/gpugems_ch08.html
// But with values optimised to match as close as possible the visible spectrum
// Fits this: https://commons.wikimedia.org/wiki/File:Linear_visible_spectrum.svg
// With weighter MSE (RGB weights: 0.3, 0.59, 0.11)
vec3 bump3y (vec3 x, vec3 yoffset)
{
	vec3 y = vec3(1.,1.,1.) - x * x;
	y = saturate(y-yoffset);
	return y;
}
vec3 spectral_zucconi (float w)
{
    // w: [400, 700]
	// x: [0,   1]
	float x = saturate((w - 400.0)/ 300.0);

	const vec3 cs = vec3(3.54541723, 2.86670055, 2.29421995);
	const vec3 xs = vec3(0.69548916, 0.49416934, 0.28269708);
	const vec3 ys = vec3(0.02320775, 0.15936245, 0.53520021);

	return bump3y (	cs * (x - xs), ys);
}


///// SDF FUNCION /////

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sdDeathStar( vec3 p2, float ra, float rb, float d )
{
  // sampling independent computations (only depend on shape)
  float a = (ra*ra - rb*rb + d*d)/(2.0*d);
  float b = sqrt(max(ra*ra-a*a,0.0));

  // sampling dependant computations
  vec2 p = vec2( p2.x, length(p2.yz) );
  if( p.x*b-p.y*a > d*max(b-p.y,0.0) )
    return length(p-vec2(a,b));
  else
    return max( (length(p            )-ra),
               -(length(p-vec2(d,0.0))-rb));
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

vec3 opRep( in vec3 p, in vec3 c)
{
    return mod(p+0.5*c,c)-0.5*c;
}

float opSmoothSubtraction( float d1, float d2, float k )
{
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

///// RANDOM FUNCTION /////

//quick and poor function
float frand(vec2 st)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(st.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

vec2 hash23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}


///// SAMPLING FUNCTION ////

vec3 cosineWeightedhemisphereSampling(vec3 normal, vec2 uv)
{
    //cosine Weighted hemisphere Sampling
    float a = 1.0 - 2.0*frand(uv.xy);
    float b = sqrt(1.0 - a*a);
    float phi = 2.0*PI*frand(-uv.yx);

    vec3 dir = vec3(0.0);

    dir.x = normal.x + b*cos(phi);
    dir.y = normal.y + b*sin(phi);
    dir.z = normal.z + a;

    return dir;
}

#define MAX_STEP 200
#define MAX_DIST 1000.0


///// RAY MARCHING FUNCTION /////

//Map the model for displaying BxDF
vec2 mapModel(vec3 pos)
{

    vec3 mpos = pos - vec3(0.0,1.6,0.0);

    mpos.xy *= rot(-30.0);

    //outside sphere
    float dstar = sdDeathStar(mpos,1.1,0.75,1.3);
    vec2 dm = vec2(dstar,1.5); 

    //little blob sphere
    float blob = length(mpos - vec3(0.95,0.0,0.0)) - 0.25;

    dm.x = min(dm.x,blob);

    //interior sphere
    float interiorSphere = length(mpos) - 1.0;

    if(dm.x > interiorSphere) dm.y = 3.5;
    dm.x = min(dm.x,interiorSphere);



    //baseplate
    float miniBox = sdBox(pos - vec3(0.0,0.5,2.0), vec3(0.5));
    miniBox = min(sdBox(pos - vec3(2.0,0.5,0.0), vec3(0.5)),miniBox);
    miniBox = min(sdBox(pos - vec3(0.0,0.5,-2.0), vec3(0.5)),miniBox);
    miniBox = min(sdBox(pos - vec3(-2.0,0.5,0.0), vec3(0.5)),miniBox);
    float roundCyl = sdRoundedCylinder(pos, 1.0, 0.25, 0.25 );

    float pedestal = opSmoothSubtraction(miniBox, roundCyl, 0.15);


    if(dm.x > pedestal) dm.y = 0.5;
    dm.x = min(dm.x,pedestal);

    return dm;
}

//Map the whole scene
vec2 map(vec3 pos)
{
    vec2 dm = vec2(0.0,1.5);

    dm = mapModel(pos);

    float ground = pos.y-0.001;
    if( dm.x > ground) dm.y = 2.5;
    dm.x = min(dm.x,ground);

    return dm;
}

// Raymarching loop to trace the path of a ray in the scene
hitPayload trace(hitPayload p) {
    // Initialize ray distance
    p.dist = 0.0;

    // Iterate through a fixed number of steps
    for (int i = 0; i < MAX_STEP; i++) {
        // Calculate position along the ray
        p.hitPos = p.orig + p.dist * p.dir;

        // Get distance to nearest object and debugging information
        vec2 tmp = map(p.hitPos);

        // If ray is very close to an object, return hitPayload
        if (tmp.x < 0.0001)
            return p;

        // Update hitPayload with current step information
        p.nbStep = i;
        p.dist += tmp.x;
        p.matIndex = int(tmp.y);

        // Terminate loop if ray travels beyond maximum distance
        if (p.dist > MAX_DIST) {
            p.matIndex = -1; // Set material to -1 to indicate no intersection
            break;
        }
    }

    // Return final hitPayload after raymarching
    return p;
}


///// SHADING FUNCTION /////

vec3 calcNormal(vec3 pos)
{
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

// Get the maetrial of the scene of the scene
// To change the material structur go in Commun tab
// by default the is albedo, IOR, metalness and rougthness
material getMaterial(hitPayload p)
{
    // Calculate base color using sky gradient
    vec3 color = vec3(1.0) * 1.0;
    //vec3 color = vec3(0.5, 0.7, 1.0) - max(p.dir.y, 0.0) * 0.75;

    // Initialize material with default values
    material mat = material(color, 0.0, 1.0, 1.0);

    //base of the model
    if(p.matIndex == 0)
        mat.albedo = vec3(1.0,0.35,0.10);

    //outside of the shpere
    if(p.matIndex == 1)
        mat.albedo = vec3(0.10,0.70,0.3);

    //floor
    if(p.matIndex == 2)
    {

        float zoom=.5;  
        p.hitPos.xz=fract( p.hitPos.xz*zoom)*2.;
        if(floor(p.hitPos.x)>.5==floor(p.hitPos.z)>.5)
            mat.albedo = vec3(0.6);
        else
            mat.albedo = vec3(0.8);
    }

    //inside of the sphere
    if(p.matIndex == 3)
    {
        mat.albedo = vec3(0.20);
    }

    return mat;
}


///// RENDERING FUNCTION /////

vec3 BRDFLambert(material mat, hitPayload p)
{
    //Lambert BRDF : bdrf * dot(n,rd) / pdf;
    float pdf = dot(p.normal,p.dir)/PI;
    vec3 brdf = mat.albedo/PI;
    return brdf * dot(p.normal,p.dir) * (1.0/pdf);
}

mat4 getInvViewMatrix(vec3 ro, vec3 at)
{
    vec3 ww = normalize(at-ro); //front
    vec3 uu = normalize(cross(ww, vec3(0.0,1.0,0.0))); // rigth;
    vec3 vv = normalize(cross(uu,ww)); // up;

    return mat4(uu,0.,
                vv,0.,
                -ww,0.,
                0.,0.,0.,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy - 1.0)/iResolution.y;

    //jitter
    uv += (dFdy(uv.x)+dFdy(uv.y)) * hash23(vec3(mod(iTime+1.0,10.0) * uv.x, iTimeDelta*uv.y, float(iFrame)));

    //init camera
    float angle = 15.0*iMouse.x/iResolution.x;
    float height= 2.0*iMouse.y/iResolution.y;

    //if screen has not been clicked yet
    if(iMouse.x == 0.0 )
    {
        angle = 12.85;
        height = 0.70;

    }

    //camera parameter
    vec3 ro = vec3(4.0 * cos(angle), 4.0*height, 4.0 * sin(angle));
    vec3 at = vec3(0.0,0.15,0.0);
    vec3 rd = normalize(getInvViewMatrix(ro,at) * vec4(uv,-1.5,1.0)).xyz;

    //Using a struct make the code cleaner and
    //will also shorten many function 
    hitPayload p = hitPayload(
        ro, //postion aka origin at first
        ro,
        rd,
        vec3(0.0),
        -1, 
        0,
        0.0
    );


    //global illumination bounces
    vec3 energy = vec3(1.0);
    vec3 color = vec3(0.0);
    float spp = 1.0;
    vec2 id = vec2(1.0);

    vec3 hitPos = vec3(0.0);

    //pathtracing loop
    for(int i = 0; i <5; i++)
    {
        //preform ray-marching
        p = trace(p);

        //compute normal and get color
        p.normal = calcNormal(p.orig + p.dist * p.dir);
        material mat = getMaterial(p);

        //lambert BRDF
        energy = energy * BRDFLambert(mat, p);


        //bail out early hif it the sky
        if(p.matIndex < 0)
        {
            color = energy;
            break;
        }

        //compute new bounce
        p.dir = normalize(cosineWeightedhemisphereSampling(p.normal, uv + iTimeDelta + mod(iTime,30.0)));
        p.orig = p.hitPos + p.normal*0.001;

    }


    if(iMouse.z < 0.0 || iMouse.x == 0.0)
    {
        uv = fragCoord.xy/iResolution.xy;
        spp += texture( iChannel0, uv ).w;
        color += texture( iChannel0, uv).xyz;
    }


    fragColor = vec4(color,spp);
}
