// CC0: Glowing Walls
//  Some weekend tinkering

// License: Unknown, author: Unknown, found: don't remember
float hash(vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,58.233))) * 13758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 pp = -1. + 2. * fragCoord/iResolution.xy;
  vec2 p  = pp;
  p.x *= iResolution.x/iResolution.y;
  vec3 
      col   = vec3(0)
    , ro    = vec3(0,0,iTime)
    , rd    = normalize(vec3(p,2))
    , ard   = abs(rd)
    , srd   = sign(rd)
    ;

  for (float i = 1.; i < 10.; ++i) {
    float tw = -(ro.x-6.*sqrt(i))/ard.x;

    vec3 wp = ro+rd*tw;

    vec2 
        wp2 = (ro+rd*tw).yz*2E-2
      , wn2 = round(wp2)
      , wc2 = wp2 - wn2
      ;

    if (hash(wn2+i+.5*srd.x) < .5)
      wc2 = vec2(wc2.y, -wc2.x);

    float 
        fo  = smoothstep(-sqrt(.5), 1., sin(.1*wp.z+iTime+i+srd.x))
      , wd  = abs(min(length(wc2+.5)-.5, length(wc2-.5)-.5))-25E-3
      ;


    col += 
       (1.+sin(vec3(-4,3,1)/2.+5E-2*tw+iTime))
      *exp(-3E-3*tw*tw)
      *fo
      *25E-4/max(abs(wd), 3E-3*fo);
  }

  col *= smoothstep(sqrt(2.), sqrt(.5), length(pp));
  col = sqrt(tanh((col)));

  fragColor = vec4(col, 1.);
}
