// oklab was invented by Björn Ottosson: https://bottosson.github.io/posts/oklab

vec3 oklab_mix2( vec3 col, float h1, float h2 ) {
    const mat3 kCONEtoLMS = mat3(                
         0.4121656120,  0.2118591070,  0.0883097947,
         0.5362752080,  0.6807189584,  0.2818474174,
         0.0514575653,  0.1074065790,  0.6302613616);
    const mat3 kLMStoCONE = mat3(
         4.0767245293, -1.2681437731, -0.0041119885,
        -3.3072168827,  2.6093323231, -0.7034763098,
         0.2307590544, -0.3411344290,  1.7068625689);

    vec3 lms = pow( kCONEtoLMS*col, vec3(1.0/3.0) );
    lms = mix(vec3(1.0), lms, h1);
    lms = mix(vec3(0.0), lms, h2);
    vec3 CONE = kLMStoCONE*(lms*lms*lms);
    return CONE;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord / iResolution.xy;
    vec3 phase = iTime-vec3(4.0, 2.0, 0.0);
    vec3 col = 0.5+0.5*cos(phase);
    col = pow(col,vec3(2.2));
    col = oklab_mix2(col, uv.x, uv.y);
    col = pow(col,vec3(1.0/2.2));
    fragColor = vec4(col, 1.0);
}
