/**
 * @author rah, inspired by jonobr1 / http://jonobr1.com/
 */

/* 
 * Output circle colors at given center pos and radius
 */
vec4 circle(vec2 uv, vec2 center, float radius, vec3 color)
{
    // Get distance of point from center, get difference from given radius
	float d = length(center - uv) - radius;
	float t = clamp(d, 0.0, 1.0);

    // If point is smaller than radius, set color alpha to 1, otherwise 0
	return vec4(color, 1.0 - t);
}

/*
 * Your work here!
 */
float computeRadius(vec2 uv)
{
    float radius = 0.25 * iResolution.y;

    // Centered uv
    vec2 uvCenter = (2.0f * uv - iResolution.xy);
    float c = mod(iTime, 3.0f);
    float r1 = impulse(2.7f,c);
    // Get pixel angle around the center
    float a = atan(uvCenter.x,uvCenter.y);
    return radius * (sin(a*24.0f) * 0.3f * r1 + 1.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = computeRadius(uv);

    // Background layer
	vec4 layer1 = vec4(rgb(255.0, 215.0, 210.0), 1.0);

	// Circle
	vec3 red = rgb(225.0, 50.0, 70.0);
	vec4 layer2 = circle(uv, center, radius, red);

	// Blend the two
	fragColor = mix(layer1, layer2, layer2.a);

}

/*
 * Toolbox functions! ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */ 
float bias(float t, float b) {
    return (t / ((((1.0/b) - 2.0)*(1.0 - t))+1.0));
}

/*
 * Basic Utilities ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 */


/* 
 * Rotate an input point about some center by angle
 */
vec2 rotatePoint2d(vec2 uv, vec2 center, float angle)
{
    vec2 rotatedPoint = vec2(uv.x - center.x, uv.y - center.y);
    float newX = cos(angle) * rotatedPoint.x - sin(angle) * rotatedPoint.y;
    rotatedPoint.y = sin(angle) * rotatedPoint.x + cos(angle) * rotatedPoint.y;
    rotatedPoint.x = newX;
    return rotatedPoint;
}

/*
 * Make a vec3 color from rgb values [0, 255]
 */
vec3 rgb(float r, float g, float b)
{
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}


float impulse(float k, float x)
{
    float h = k * x;
    return h * exp(1.0f - h);
}
