void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate color variations over time using sine function
    float t = iTime * 2.0;
    vec3 colorVariation = vec3(sin(t), sin(t + 2.0), sin(t + 4.0));

    // Sample texture
    vec3 texColor = texture(iChannel0, uv).xyz;

    // Apply color variations to the sampled texture color
    vec3 finalColor = texColor * colorVariation;

    fragColor = vec4(finalColor, 0.9);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy;
    // if mouse not detected do something
    if(mouse.x <= 0.) mouse = vec2( iResolution.x * (sin(iTime)+1.)/2., iResolution.y/2.);

    vec2 tuv = fragCoord.xy/iResolution.xy;

    vec3 c = vec3(.05/length((fragCoord.xy - mouse.xy) / iResolution.y));
    vec3 fb = texture(iChannel0, tuv).xyz;

    c = mix(c, fb, 0.9);

    fragColor = vec4(c, 1.0);
}
