        uv = (uv-t2*2.)/scale;
        uv = ((vec2(uv+vec2(.5,1.5))*scale)-.5)/scale;

        t2 = triangle_wave(uv+.5);
        uv =
            t2-triangle_wave(uv.yx)
            //1./2.-triangle_wave(uv.yx)
        ;

        {t2.x = (t2.x+sign(t2.y-t2.x)); }
        if(uv.x>uv.y)
        uv.y -= 1./4.;
}

