}

vec2 flute(int a,float b,float c,float duration){
    return
        mainSound1(a,b*2.,c,duration)+mainSound1(a,b*2.,c,duration/2.)
        -mainSound1(a,b*2.,c+1.,duration/2.)+mainSound1(a,b*2.,c+1.,duration/4.)
    ;
}

vec2 oud(int a,float b,float c,float duration){
    return
        mainSound1(a,b*2.,c,duration)-mainSound1(a,b*2.,c,duration/2.)/2.
