void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    vec2 point1 = iMouse.xy;
    vec2 point2 = abs(iMouse.zw);


    float dist1 = 1.0 - distance(point1, uv)/256.0;
    float dist2 = 1.0 - distance(point2, uv)/256.0;
    float state = 0.0;

    if(uv.y < 100.0)
    {

        if (uv.x < 50.0)
        {
            if(iMouse.x < 0.0)
                state = 1.0;
        }
        else if (uv.x < 100.0)
        {
            if(iMouse.y < 0.0)
                state = 1.0;
        }
        else if (uv.x < 150.0)
        {
            if(iMouse.z < 0.0)
                state = 1.0;
        }
        else if (uv.x < 200.0)
        {
            if(iMouse.w < 0.0)
                state = 1.0;
        }
    }
    fragColor = vec4(dist1, dist2, state, 1.0);
}
