#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(23,43,21))*2.5+.5)


#define hr vec2(1., sqrt(3.))
#define PI 3.141592
#define TAU (2.*PI)
#define time (iTime*0.5)

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.4,18.4)))*1245.4);}

mat2 rot (float a)
{return mat2 (cos(a),sin(a),-sin(a),cos(a));}

void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y>p.x) p = p.yx;
}

float stmin (float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

float hd (vec2 uv)
{
    uv = abs(uv);
    return max(uv.x, dot(uv, normalize(hr)));
}




float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z))) + length(max(q,0.));
}

float fractal (vec3 p)
{
    float size = 1.;
    float d = box(p,vec3(size));
    for (float i=0.; i<5.; i++)
    {
        float ratio = i/5.;

        mo(p.xz, vec2(2.+ratio));
        mo(p.xy, vec2(1.5+ratio));
        p.xy *= rot(time+ratio);
        size -= ratio*1.5;
        d= stmin(d,box(p,vec3(size)),1., 4.);
    }
    return d;
}

float g1 = 0.;
float SDF (vec3 p)
{
    float d = fractal(p);
    g1 += 0.1/(0.1+d*d);
    return d;
}
float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec2 uv = (2.*C-iResolution.xy)/iResolution.y;

    float mask = step(0.3, abs(sin(length(uv)-PI*time))+0.01);


    float dither = hash21(uv);

    vec3 ro = vec3(0.,0.,-10.),
        p3 = ro,
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.);

    float shad = 0.;

    for (float i=0.; i<64.; i++)
    {
        float d = SDF(p3);
        if (d<0.01)
        {
            shad = i/64.;
            break;
        }
        d *= 0.7+dither*0.1;
        p3 += d*rd;
    }

    col = vec3(0.1,length(uv*0.5),0.7)*g1*0.2;
    col *= (1.);

    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        p=g*d;


        a=20.;
        p=mod(p-a,a*2.)-a;
        s=2.;
        for(int i=0;i++<8;){
            p=.3-abs(p);

            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
            p.y<p.x?p=p.zyx:p;

            s*=e=1.4+sin(iTime*.234)*.1;
            p=abs(p)*e-
                vec3(
                    5.+cos(iTime*.3+.5*cos(iTime*.3))*3.,
                    120,
                    8.+cos(iTime*.5)*5.
                 )*col;
         }

         g+=e=length(p.yzx)/s;
    }
     uv *= 2.0 * ( cos(iTime * 2.0) -2.5); // scale
    float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1 
    O*= vec4(happy_star(uv, anim) * vec3(0.35,0.2,0.55)*25., 1.0);
}
