#define sat(x) clamp(x,0.0,1.0)

mat2 rot(float a){return mat2(cos(a),-sin(a),sin(a),cos(a));}

void mainImage(out vec4 fragColor,vec2 fragCoord){
	vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	vec3 col = vec3(0.0);

	vec3 ro = vec3(0,1,3);
	vec3 rd = normalize(vec3(uv,-1.5));
    //orbit camera
    rd.yz *= rot(-0.25);
	ro.xz *= rot(iTime * 0.5);
	rd.xz *= rot(iTime * 0.5);

    //color
    float k = 0.5+0.5*sin(iTime);
	vec3 ec = normalize(mix(mix(vec3(1,0.1,0.4),vec3(0.7,0.1,1),k*2.0),vec3(0.1,1,0.8),k));

    float t = 0.0;
	vec3 ol = vec3(0.0); //outline
	for(int i=0;i<256;i++){
		vec3 p = ro + rd * t;
		float h = 999999.999;

        //sdf scene
        h = min(h,length(p)-0.5);
		h = min(h,length(p-0.5)-0.4);
		h = max(h,-(length(p-vec3(0,0.8,0))-0.9));
		h = min(h,length(p-0.5)-0.2);
		h = min(h,length(p+0.5)-0.2);

        ol += (ec*0.5+0.5)*0.01;
		if(t > 100.0) break;
		if(h < 0.0002){ol = vec3(1); break;}
		t += h;
	}

    //result
	col += ec*sat(pow(ol*4.5,vec3(4.0)));

    fragColor = vec4(col,1.0);
}
