#define MAIN_CIRCLE_SIZE 0.19
#define GRID_ROWS 4.0
#define GRID_COLS 6.0

float createCircle(vec2 uv, vec2 center) {
    float dist = length(uv - center);
    float circle = 1.0 - step(MAIN_CIRCLE_SIZE, dist);
    float pattern = sin(dist / MAIN_CIRCLE_SIZE * 20.0 + iTime) * 0.5 + 0.5;
    return circle * pattern;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = (uv - 0.5) * 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float result = 0.0;

    for (float row = 0.0; row < GRID_ROWS; row++) {
        for (float col = 0.0; col < GRID_COLS; col++) {
            vec2 center = vec2(
                (col + 0.5) / GRID_COLS - 0.5,
                (row + 0.5) / GRID_ROWS - 0.5
            ) * 2.0;
            center.x *= iResolution.x / iResolution.y;

            result += createCircle(uv, center);
        }
    }

    result = clamp(result, 0.0, 1.0);
    vec3 finalColor = vec3(result);
    fragColor = vec4(finalColor, 1.0);
}
