// --- reproducing https://twitter.com/Yugemaku/status/1741672931128918476
// variant of https://shadertoy.com/view/4cBGDW
// drawing each segment only once

// simplified from "Compound of Five Tetrahedra" by mla. https://shadertoy.com/view/tsXyWs
vec3 V[] = vec3[](
         vec3( 0  , 0  , 1   ), 
         vec3( .58, .33, .75 ), 
         vec3( 0  ,-.67, .75 ), 
         vec3(-.58, .33, .75 ),
         vec3( .36, .87, .33 ),
         vec3( .93,-.13, .33 ), 
         vec3( .58,-.75, .33 ), 
         vec3(-.58,-.75, .33 ), 
         vec3(-.93,-.13, .33 ), 
         vec3(-.36, .87, .33 ), 
         vec3( .58, .75,-.33 ),
         vec3( .93, .13,-.33 ), 
         vec3( .36,-.87,-.33 ), 
         vec3(-.36,-.87,-.33 ), 
         vec3(-.93, .13,-.33 ),  
         vec3(-.58, .75,-.33 ),  
         vec3( 0  , .67,-.75 ),  
         vec3( .58,-.33,-.75 ), 
         vec3(-.58,-.33,-.75 ), 
         vec3( 0  , 0  ,-1   ) 
      );
              // full faces, so each edge might be drawn twice
int S[] = int[] (    0, 2, 6, 5, 1  // 3 bottom
                   , 0, 3, 8, 7, 2
                   , 0, 1, 4, 9, 3
                   , 2, 7,13,12, 6  // 6 crown
                   , 8,14,18,13, 7
                   , 6,12,17,11, 5
                   , 3, 9,15,14, 8
                   , 1, 5,11,10, 4 
                   , 4,10,16,15, 9
                   ,19,18,14,15,16  // 3 top
                   ,19,17,12,13,18
                   ,19,16,10,11,17
  );
#define S(i) S[clamp(i,0,59)]

float segment(vec2 p, vec2 a, vec2 b) {
    p -= a;
    b -= a;
    return length(p - b *  clamp(dot(p, b) / dot(b, b), 0., 1.) );
}

#define R      iResolution.xy
#define rot(a) mat2(cos(a+vec4(0,33,11,0)))

vec2 T(vec3 p) {
    vec2 M = iMouse.xy;          // camera rotation
    M = length(M) > 10.
      ? ( 2.*M/R - 1. ) * 3.14
      : iTime/vec2(8,-5);
    p.yz *= rot( M.y),  // theta
    p.zx *= rot(-M.x);  // phi

    return p.xy / ( p.z - 1.7 ); // perspective projection
}

#define rot3(P,A,a) ( mix( A*dot(P,A), P, cos(a) ) + sin(a)*cross(P,A) )

void mainImage( out vec4 O, vec2 U ) {
    U = ( 2.*U - R.xy ) / R.y;
    O = vec4(1);

    for( int i,i0,i1; i <= S.length(); i++ ) {
        vec3 A = V[ i0 = S(i-1) ],
             B = V[ i1 = S(i%5 > 0 ? i : i-5) ],
             M = normalize(A+B);
       A = rot3(A,M,.15); // tilting
       B = rot3(B,M,.15);
       if ( i1 > i0 ) // draw segments only once 
           O -= smoothstep(2./R.y, -2./R.y, segment( U, T(A), T(B) ) -.007 );
    }
}
