// webGL2 regolfing + variant of 327 chars https://shadertoy.com/view/MlXXzH
// reusable function below

/** // 252 chars version by Xor

#define D(P, N) P.y > 5 || u.x < .35 || u.x > .9 ? 0 : int[](0,972881535,696556137,704642687,690407533,972980223)[P.y] >> 30 -3*(N) - P.x &1

void mainImage(out vec4 o, vec2 u) {
    u /= 48.; 
    float n = floor(u.x);
    u.x -= n - .125;
    o = vec4( n > 7. || D( ivec2(u*4.), int(iDate.w*pow(10.,n-4.)) % 10) );
}




/**/   // -6 by xor

int D( vec2 p, int n ) { // --- char font decoding 
    return p.y > 6. || abs( p.x - 2. ) > 1.1 ? 0
              : int[](0,972881535,696556137,704642687,690407533,972980223)[int(p.y)]
                >> 30 -3*n - int(p+.5)           //  extract pos bit
                &1;                              // 1:ink 0:paper 
}

// -24 Xor variant, getting nicer digits: 
//#define D(P, N) P.y > 6. || P.x < 1. ? 0 : int[](0,972881535,696556137,704642687,690407533,972980223)[int(P.y)] >> 30 -3*(N) - int(P) &1

 void mainImage(out vec4 o, vec2 u) {
    u /= 12.; 
    float n = floor(u.x/4.);               // digit location
           // float((int(u)+1)/4);
    u.x -= 4.*n;
    o = vec4( n < 8. ? D( u, int(iDate.w*pow(10.,n-4.)) % 10 ) : 0 );
}

