// Fork of "Trippy Colour Hypno" by NinZargo. https://shadertoy.com/view/lc23W1
// 2024-01-02 14:14:24
vec3 palette(float t){
    vec3 a = vec3(0.718, 0.500, 0.500);
    vec3 b = vec3(0.498, 0.318, 0.700);
    vec3 c = vec3(0.568, 1.118, 1.000);
    vec3 d = vec3(-2.042, 0.333, 0.667);

    return a +b*cos(6.28318*(c*t*d));
}

vec3 offset( vec2 uv, float offset, vec2 uv0) 
{
    float a = length(uv) *exp(-length(uv0));

    float bounceValue = sin((iTime * offset)*0.03); // Using sin for oscillation between -1 and 1
        bounceValue = bounceValue * 0.5 + 0.5; // Scale and shift to fit between 0 and 1
        bounceValue *= 5.0;

    a = sin(a*8.0 - (bounceValue))/8.0; // Diameter of circle
    a = abs(a);

    //a = step(0.1, a);
    a = pow(0.015/a, 1.5);

    vec3 col = palette(length(uv0) + iTime*.4);

    return col * a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Setup
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y; // Normalized pixel coordinates regardless of aspect(from 0 to 1)

    vec3 finalColor = vec3(0.);

    vec2 uv0 = uv;

    for(float i = 0.0; i < 2.0; i++){
         uv = fract(uv *1.2) - 0.5;

         for(float j = 0.; j<3.; j++){
             finalColor += (offset(uv, j*3., uv0) * palette(j*5.));
         }

    }

    fragColor = vec4(finalColor, 1.0); // x, y, z, alpha

}
