vec4 grading(vec4 col, float powVal) {
    return vec4(pow(col.x, powVal), pow(col.y, powVal), pow(col.z, powVal), 1.0);
}

vec3 psychedelicColor(float c1, float c2) {
    return vec3(sin(c1 * c2*0.3 + iTime)*0.5+0.5, sin(4.32*(c2 - c1) + iTime*3.45)*0.5+0.5, sin((c1 + c2)*7.43 + iTime*5.31)*0.5+0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iChannelResolution[0].xy;
    //vec2 uvBig = (((uv - 0.5  + 0.01*noise(vec3(uv*23.1, iTime*1.4))) * 2.0) + 0.5);
    vec2 uvBig = ((uv - 0.5)*1.005)+0.5 + 0.007*(noise(vec3(uv*23.1 - iTime*0.4, iTime*1.4)) - 0.5);
    float color = mod(uvBig.x + sin(iTime*0.1), 0.05) / 0.05 + noise(vec3(uv*10.0, iTime));
    float color2 = mod(uvBig.y + cos(iTime*0.1), 0.05) / 0.05 + noise(vec3(uv*10.0+12.3, iTime));
    vec3 colorPsychedelic = psychedelicColor(color, color2);
    fragColor = grading(vec4(colorPsychedelic, 1.0) * 0.02 + texture(iChannel0, uvBig) * 0.98, 0.95);
}
